mapper.select:
select * from t_api_config
where 1=1

	and versionCode=#{versionCode}


	and hostCode=#{hostCode}


	and apiPath=#{apiPath}

order by id limit 1

select id,hostCode,hostName,name from t_api_config
where 1=1

	and versionCode=#{versionCode}


	and hostCode=#{hostCode}

order by id

select top 1 * from t_api_config
where 1=1

	and versionCode=#{versionCode}


	and hostCode=#{versionCode}


	and apiPath=#{apiPath}
